import { useState } from "react";
import loginImg from "./loginImg.jpg";

const peopleData = [
  { id: 1, name: "Alice Johnson" },
  { id: 2, name: "Bob Smith" },
  { id: 3, name: "Charlie Davis" },
  { id: 4, name: "Diana Prince" },
  { id: 5, name: "Edward Snowden" }
];

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [search, setSearch] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "password") {
      setLoggedIn(true);
    } else {
      alert("Invalid credentials");
    }
  };

  const filteredPeople = peopleData.filter(person =>
    person.name.toLowerCase().includes(search.toLowerCase())
  );

  if (!loggedIn) {
    return (
      <div className="min-h-screen flex">
        {/* Left: Login Form */}
        <div className="w-1/2 flex items-center justify-center bg-gray-50">
          <form
            onSubmit={handleLogin}
            className="bg-white p-8 rounded-2xl shadow-md w-full max-w-sm"
          > 

            <h2 className="text-2xl mb-2 text-left">Welcome ðŸ‘‹</h2>
            <h2 className="text-sm text-gray-400 mb-10 text-left font-extralight">Please login with your health username and password, to continue.</h2>
            <h2 className="text-sm text-gray-400 text-left font-extralight">Please login with your health username and password, to continue.</h2>
            <input
              type="text"
              placeholder="Username"
              className="w-full mb-4 px-4 py-2 border rounded focus:outline-none focus:ring"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              className="w-full mb-6 px-4 py-2 border rounded focus:outline-none focus:ring"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button
              type="submit"
              className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded"
            >
              Login
            </button>
          </form>
        </div>

        {/* Right: Image */}
        <div className="w-1/2 flex items-center justify-center p-6 bg-gray-50">
          <img
            src={loginImg}
            alt="Login Illustration"
            className="w-[90%] h-[90vh] shadow-lg rounded-2xl"
          />
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-md mx-auto bg-white p-6 rounded-2xl shadow">
        <h1 className="text-2xl font-bold mb-4 text-center">People Search</h1>
        <input
          type="text"
          placeholder="Search by name..."
          className="w-full border border-gray-300 rounded px-4 py-2 mb-4 focus:outline-none focus:ring-2 focus:ring-blue-400"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <ul>
          {filteredPeople.length > 0 ? (
            filteredPeople.map(person => (
              <li
                key={person.id}
                className="p-2 border-b border-gray-200 text-gray-800"
              >
                {person.name}
              </li>
            ))
          ) : (
            <li className="text-gray-500">No matches found.</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default App;
